// Um den kurzen _()-Aufruf zu verwenden, f√ºgen Sie diese Zeilen hinzu
using static Ankonoanko.Localization.LocalizationManager;
using Ankonoanko.Localization; // Erforderlich f√ºr [OnLanguageChange]
using UnityEngine;
using TMPro;
using System.Collections.Generic;
public class PlayerHUD : MonoBehaviour
{
    [SerializeField] private TMP_Text scoreText;
    private int score = 100;
    private string playerName = "Alex";

    void Start()
    {
        UpdateUI(); // Erstes Update
    }

    // Diese Methode wird automatisch aufgerufen, wenn sich die Sprache √§ndert
    [OnLanguageChange]
    void UpdateUI()
    {
        // 1. Indizierte Platzhalter (wie in string.Format)
        // Schl√ºssel: "score_label", Text in Datei: "Score: {0}"
        scoreText.text = _("score_label", score); // Ergebnis: "Score: 100"

        // 2. Benannte Platzhalter (empfohlen f√ºr bessere Lesbarkeit)
        // Schl√ºssel: "welcome_message", Text: "Welcome, {username}!"
        string welcomeText = _("welcome_message", new { username = playerName });

        // F√ºr komplexe F√§lle oder hohe Leistung
        var args = new Dictionary<string, object> { { "username", playerName } };
        string welcomeText2 = _("welcome_message", args);
    }
}

