using Ankonoanko.Localization;
using UnityEngine;
using System.Collections.Generic;

public class HeroDialog : MonoBehaviour
{
    // 1. Private fields work perfectly
    [LocalizableField]
    private string secretWhisper = "It's a secret to everybody.";

    // 2. Custom Key Override
    [LocalizableField("hero_greeting_main")]
    public string greeting = "Welcome, traveler!";

    // 3. Lists and Arrays (Keys become: inventory_header_0, inventory_header_1...)
    [LocalizableField]
    public string[] inventoryHeaders = new string[] { "Weapons", "Potions", "Armor" };

    // 4. Nested Serializable Classes (Recursion supported)
    [System.Serializable]
    public class BossInfo
    {
        [LocalizableField] public string name;
        [LocalizableField] public string title;
    }
    public BossInfo currentBoss; // Parser drills down into this

    // 5. List of Nested Objects
    [LocalizableField]
    public List<BossInfo> bossList = new List<BossInfo>();

    // 6. Custom Key on List (Keys: dungeon_hints_list_0, dungeon_hints_list_1...)
    [LocalizableField("dungeon_hints_list")]
    public List<string> dungeonHints = new List<string>
    {
        "Beware of traps.",
        "Use fire against trolls."
    };

    // 7. Interpolated Strings (Parser reads the raw format string)
    [LocalizableField("fire_sword_name")]
    public string ItemName1 = $"Fire Sword {0}";
}

