using static Ankonoanko.Localization.LocalizationManager;
using Ankonoanko.Localization; // Necesario para [OnLanguageChange]
using UnityEngine;
using TMPro;
using System.Collections.Generic;
public class PlayerHUD : MonoBehaviour
{
    [SerializeField] private TMP_Text scoreText;
    private int score = 100;
    private string playerName = "Alex";

    void Start()
    {
        UpdateUI(); // Actualizaci칩n inicial
    }

    // Este m칠todo se llamar치 autom치ticamente cuando cambie el idioma
    [OnLanguageChange]
    void UpdateUI()
    {
        // 1. Marcadores de posici칩n indexados (como en string.Format)
        // Clave: "score_label", Texto en el archivo: "Score: {0}"
        scoreText.text = _("score_label", score); // Resultado: "Score: 100"

        // 2. Marcadores de posici칩n con nombre (recomendado para legibilidad)
        // Clave: "welcome_message", Texto: "Welcome, {username}!"
        string welcomeText = _("welcome_message", new { username = playerName });

        // Para casos complejos o alto rendimiento
        var args = new Dictionary<string, object> { { "username", playerName } };
        string welcomeText2 = _("welcome_message", args);
    }
}

