#if UNITY_EDITOR
using System.Collections.Generic;
using System.Text.RegularExpressions;
using UnityEngine;
using Ankonoanko.Localization.Next;

// TEMPLATE for creating a parser for a custom component.
public class CustomComponentParser_Template : ITextComponentParser
{
    public IEnumerable<(string text, string key, string source)> Parse(
        GameObject gameObject,
        LocalizationSettings settings,
        string prefabContextPath,
        Dictionary<string, string> textToKeyMap = null,
        List<Regex> ignoreRegexes = null,
        bool migrationActive = false)
    {
        // 1. Check if the object has the custom component we need.
        var component = gameObject.GetComponent<AwesomeComponentFromAssetStore>();
        if (component == null)
        {
            yield break; // Exit if the component is not found
        }

        // 2. Get the text from the custom component's fields.
        string titleText = component.Title;

        // 3. Check if the text is valid for localization.
        // We use ParsingLogic helper to check against ignore patterns
        if (ParsingLogic.IsValidLocalizableText(titleText, ignoreRegexes))
        {
            // 4. Generate the key and source using helpers from ParsingLogic.
            string key = ParsingLogic.GenerateKeyForObject(gameObject, titleText, settings.keyGenerationMode, prefabContextPath, null, textToKeyMap);
            string source = ParsingLogic.GetSourceStringForObject(gameObject, prefabContextPath);

            // 5. Return the result.
            yield return (titleText, key, source);
        }

        // 6. Repeat for other fields...
        string descriptionText = component.Description;
        if (ParsingLogic.IsValidLocalizableText(descriptionText, ignoreRegexes))
        {
            string key = ParsingLogic.GenerateKeyForObject(gameObject, descriptionText, settings.keyGenerationMode, prefabContextPath, null, textToKeyMap) + "_desc";
            string source = ParsingLogic.GetSourceStringForObject(gameObject, prefabContextPath);
            yield return (descriptionText, key, source);
        }
    }
}
#endif

code_custom_provider_example:
using System;
using System.Collections.Generic;
using System.Threading.Tasks;
using UnityEngine;

namespace Ankonoanko.Localization.Next
{
    [AssetProviderPlugin(
        "My Custom System",  // ← Name visible to user
        "Description of your asset loading system",  // ← Short description
        Author = "Your Name",
        Version = "1.0"
    )]
    public class MyCustomProvider : IAssetProvider
    {
        private Dictionary<string, UnityEngine.Object> _loadedAssets = new();
        private string _currentLanguage;

        public string ProviderName => "My Asset System";
        public bool SupportsSyncLoading => true;
        public bool SupportsAsyncLoading => true;

        public void Initialize(LocalizationSettings settings) { /* Init system */ }

        public T Load<T>(string key) where T : UnityEngine.Object {
            if (_loadedAssets.TryGetValue(key, out var cached)) return (T)cached;
            T asset = Resources.Load<T>(key); // Replace with your logic
            if (asset != null) _loadedAssets[key] = asset;
            return asset;
        }

        public async Task<T> LoadAsync<T>(string key) where T : UnityEngine.Object {
            return await Task.Run(() => Load<T>(key));
        }

        public void Unload(string key) => _loadedAssets.Remove(key);
        public void UnloadAll() => _loadedAssets.Clear();
        public bool Exists(string key) => true;
        public void Dispose() => UnloadAll();

        public void SetCurrentLanguage(string lang) {
            _currentLanguage = lang;
            UnloadAll(); // Clear cache for new language
        }
    }
}

code_example_attribute:
using Ankonoanko.Localization.Next;
using UnityEngine;
using System.Collections.Generic;

// 1. Unattached & Base Classes (No MonoBehaviour needed)
public class BaseData { 
    [LocalizableField] protected string rarity = "Common"; 
}

public class MegaTest : BaseData // Inherited fields are scanned!
{
    // 2. Basic Fields (Public, Private, Protected)
    [LocalizableField] private string _private = "Private Field";
    [LocalizableField] public string PublicField = "Public Field";

    // 3. Properties (Auto & Full)
    [LocalizableField] public string AutoProp { get; set; } = "Auto Property";
    [LocalizableField] public string GetOnly { get; } = "Get Only Property";

    // 4. Static & Constants (Global text)
    public const string GLOBAL_CONST = "Global Const Value";
    [LocalizableField] public static string StaticField = "Static Global Text";

    // Roslyn resolves these references during scan:
    private const string LOCAL_CONST = "LocalConst";
    [LocalizableField] public string ReferenceConst = LOCAL_CONST;
    [LocalizableField] public string GlobalConstRef = GLOBAL_CONST;

    // 5. String Concatenation & Interpolation (Calculated at parse time)
    [LocalizableField] public string Concat1 = "Hello " + "World";
    [LocalizableField] public string Concat2 = "Prefix: " + LOCAL_CONST;

    // 6. Custom Keys & Attributes
    [LocalizableField("main_menu_title")] public string Title = "Game Title";
    [LocalizableField(key: "custom_key_syntax")] public string KeySyntax = "Explicit Key";

    [Header("Header Text")] // Scanned if 'Header' added to Attributes to Scan
    [Tooltip("Tooltip Text")]
    public string annotatedField;

    // 7. Collections (Arrays, Lists, Target-typed new)
    [LocalizableField] public string[] Array1 = { "A", "B" };
    [LocalizableField] public List<string> List1 = new List<string> { "C", "D" };
    [LocalizableField] public List<string> ListNew = new() { "E", "F" }; // C# 9.0+

    // 8. Nested Objects (Deep Recursion)
    [System.Serializable]
    public class DialogStep {
        [LocalizableField] public string actorName;
        [LocalizableField] public string message;
    }
    [LocalizableField] public DialogStep introStep; 
    [LocalizableField] public List<DialogStep> dialogSelect; // List of objects!
}

code_example_function:
// 1. Add Static Import for short syntax
using static Ankonoanko.Localization.Next.LocalizationManager; 

public class RuntimeExample : MonoBehaviour {
    void Demo() {
        // 2. Simple Key/Text lookup
        _("Hello World");

        // 3. Indexed Formatting ({0}, {1})
        _("Score: {0} / {1}", 10, 20); 

        // 4. Named Arguments (Smart Formatting)
        // Uses anonymous object. Key = "welcome_user"
        _("welcome_user", new { name = "Hero", level = 5 }); 

        // 5. Pluralization (Integer argument)
        // Automatically finds key_one, key_few, key_many
        _("coins_count", 5); 

        // 6. Genderization (Gender enum)
        // Automatically finds key_male, key_female
        _("npc_greeting", Gender.Female);

        // 7. Manual Dictionary (High performance / Dynamic)
        var args = new Dictionary<string, object> { { "rank", "S" } };
        _("rank_msg", args);

        // 8. Nesting (Translating arguments recursively)
        _("Element: {0}", _("Fire")); 
    }
}

