using static Ankonoanko.Localization.LocalizationManager;
using Ankonoanko.Localization; // Requis pour [OnLanguageChange]
using UnityEngine;
using TMPro;
using System.Collections.Generic;
public class PlayerHUD : MonoBehaviour
{
    [SerializeField] private TMP_Text scoreText;
    private int score = 100;
    private string playerName = "Alex";

    void Start()
    {
        UpdateUI(); // Mise √† jour initiale
    }

    // Cette m√©thode sera appel√©e automatiquement lorsque la langue change
    [OnLanguageChange]
    void UpdateUI()
    {
        // 1. Espaces r√©serv√©s index√©s (comme dans string.Format)
        // Cl√© : "score_label", Texte dans le fichier : "Score : {0}"
        scoreText.text = _("score_label", score); // R√©sultat : "Score : 100"

        // 2. Espaces r√©serv√©s nomm√©s (recommand√© pour la lisibilit√©)
        // Cl√© : "welcome_message", Texte : "Bienvenue, {username} !"
        string welcomeText = _("welcome_message", new { username = playerName });

        // Pour les cas complexes ou haute performance
        var args = new Dictionary<string, object> { { "username", playerName } };
        string welcomeText2 = _("welcome_message", args);
    }
}

