using Ankonoanko.Localization; // Requis pour [LocalizableField]
using UnityEngine;
using System.Collections.Generic;
public class QuestManager : MonoBehaviour
{
    // L'analyseur trouvera ce champ et créera une clé pour lui
    [LocalizableField]
    private string defaultQuestFailedMessage = "Vous avez échoué la quête.";

    // Vous pouvez définir une clé personnalisée
    [LocalizableField("custom_quest_start_dialog")]
    public string startDialog = "Êtes-vous prêt pour une aventure ?";

    // Fonctionne avec les listes
    [LocalizableField]
    private List<string> missionObjectives = new List<string> { "Trouver le trésor" };

    // Et même avec des classes imbriquées !
    [System.Serializable]
    public class ItemInfo
    {
        [LocalizableField] public string Name;
        [LocalizableField] public string Description;
    }

    [LocalizableField]
    private ItemInfo magicSword;
}

code_example_function:
// Pour utiliser l'appel court _(), ajoutez ces lignes
using static Ankonoanko.Localization.LocalizationManager;
using Ankonoanko.Localization; // Requis pour [OnLanguageChange]
using UnityEngine;
using TMPro;
using System.Collections.Generic;
public class PlayerHUD : MonoBehaviour
{
    [SerializeField] private TMP_Text scoreText;
    private int score = 100;
    private string playerName = "Alex";

    void Start()
    {
        UpdateUI(); // Mise à jour initiale
    }

    // Cette méthode sera appelée automatiquement lorsque la langue change
    [OnLanguageChange]
    void UpdateUI()
    {
        // 1. Espaces réservés indexés (comme dans string.Format)
        // Clé : "score_label", Texte dans le fichier : "Score : {0}"
        scoreText.text = _("score_label", score); // Résultat : "Score : 100"

        // 2. Espaces réservés nommés (recommandé pour la lisibilité)
        // Clé : "welcome_message", Texte : "Bienvenue, {username} !"
        string welcomeText = _("welcome_message", new { username = playerName });

        // Pour les cas complexes ou haute performance
        var args = new Dictionary<string, object> { { "username", playerName } };
        string welcomeText2 = _("welcome_message", args);
    }
}

code_example_plurals:
using static Ankonoanko.Localization.LocalizationManager;
using Ankonoanko.Localization;
using UnityEngine;
public class ItemCounter : MonoBehaviour
{
    void DisplayItemCount(int count)
    {
        // Clés : "apple_count_one", "apple_count_few", "apple_count_many"
        // Textes : "{0} pomme", "{0} pommes", "{0} pommes"
        string appleText = _("apple_count", count); 
        Debug.Log(appleText); // Sélectionne automatiquement la forme correcte
    }

    void GreetUser(Gender userGender)
    {
        // Clés : "user_greeted_male", "user_greeted_female"
        // Textes : "Il est arrivé.", "Elle est arrivée."
        string greeting = _("user_greeted", userGender);
        Debug.Log(greeting);
    }
}

code_custom_parser:
#if UNITY_EDITOR
using System.Collections.Generic;
using UnityEngine;
using Ankonoanko.Localization;

// MODÈLE pour créer un analyseur pour un composant personnalisé.
public class CustomComponentParser_Template : ITextComponentParser
{
    public IEnumerable<(string text, string key, string source)> Parse(
        GameObject gameObject,
        LocalizationSettings settings)
    {
        // 1. Vérifiez si l'objet a le composant personnalisé dont nous avons besoin.
        var component = gameObject.GetComponent<AwesomeComponentFromAssetStore>();
        if (component == null)
        {
            yield break; // Sortir si le composant n'est pas trouvé
        }

        // 2. Obtenez le texte des champs du composant personnalisé.
        string titleText = component.Title;

        // 3. Vérifiez si le texte est valide pour la localisation.
        if (TextParser.IsValidLocalizableText(titleText))
        {
            // 4. Générez la clé et la source à l'aide des aides de TextParser.
            string key = TextParser.GenerateKeyForObject(gameObject, titleText, settings.keyGenerationMode);
            string source = TextParser.GetSourceStringForObject(gameObject);

            // 5. Retournez le résultat.
            yield return (titleText, key, source);
        }

        // 6. Répétez pour les autres champs...
        // (un suffixe peut être ajouté pour rendre les clés uniques)
        string descriptionText = component.Description;
        if (TextParser.IsValidLocalizableText(descriptionText))
        {
            string key = TextParser.GenerateKeyForObject(gameObject, descriptionText, settings.keyGenerationMode) + "_description";
            string source = TextParser.GetSourceStringForObject(gameObject);
            yield return (descriptionText, key, source);
        }
    }
}
#endif
