#if UNITY_EDITOR
using System.Collections.Generic;
using UnityEngine;
using Ankonoanko.Localization;

// рдПрдХ рдХрд╕реНрдЯрдо рдШрдЯрдХ рдХреЗ рд▓рд┐рдП рдПрдХ рдкрд╛рд░реНрд╕рд░ рдмрдирд╛рдиреЗ рдХреЗ рд▓рд┐рдП рдЯреЗрдореНрдкрд▓реЗрдЯред
public class CustomComponentParser_Template : ITextComponentParser
{
    public IEnumerable<(string text, string key, string source)> Parse(
        GameObject gameObject,
        LocalizationSettings settings)
    {
        // 1. рдЬрд╛рдВрдЪреЗрдВ рдХрд┐ рдХреНрдпрд╛ рдСрдмреНрдЬреЗрдХреНрдЯ рдореЗрдВ рд╣рдореЗрдВ рдЖрд╡рд╢реНрдпрдХ рдХрд╕реНрдЯрдо рдШрдЯрдХ рд╣реИред
        var component = gameObject.GetComponent<AwesomeComponentFromAssetStore>();
        if (component == null)
        {
            yield break; // рдпрджрд┐ рдШрдЯрдХ рдирд╣реАрдВ рдорд┐рд▓рд╛ рддреЛ рдмрд╛рд╣рд░ рдирд┐рдХрд▓реЗрдВ
        }

        // 2. рдХрд╕реНрдЯрдо рдШрдЯрдХ рдХреЗ рдлрд╝реАрд▓реНрдб рд╕реЗ рдЯреЗрдХреНрд╕реНрдЯ рдкреНрд░рд╛рдкреНрдд рдХрд░реЗрдВред
        string titleText = component.Title;

        // 3. рдЬрд╛рдВрдЪреЗрдВ рдХрд┐ рдХреНрдпрд╛ рдЯреЗрдХреНрд╕реНрдЯ рд╕реНрдерд╛рдиреАрдпрдХрд░рдг рдХреЗ рд▓рд┐рдП рдорд╛рдиреНрдп рд╣реИред
        if (TextParser.IsValidLocalizableText(titleText))
        {
            // 4. TextParser рд╕реЗ рд╕рд╣рд╛рдпрдХреЛрдВ рдХрд╛ рдЙрдкрдпреЛрдЧ рдХрд░рдХреЗ рдХреБрдВрдЬреА рдФрд░ рд╕реНрд░реЛрдд рдЙрддреНрдкрдиреНрди рдХрд░реЗрдВред
            string key = TextParser.GenerateKeyForObject(gameObject, titleText, settings.keyGenerationMode);
            string source = TextParser.GetSourceStringForObject(gameObject);

            // 5. рдкрд░рд┐рдгрд╛рдо рд▓реМрдЯрд╛рдПрдБред
            yield return (titleText, key, source);
        }

        // 6. рдЕрдиреНрдп рдлрд╝реАрд▓реНрдб рдХреЗ рд▓рд┐рдП рджреЛрд╣рд░рд╛рдПрдБ...
        // (рдХреБрдВрдЬрд┐рдпреЛрдВ рдХреЛ рдЕрджреНрд╡рд┐рддреАрдп рдмрдирд╛рдиреЗ рдХреЗ рд▓рд┐рдП рдПрдХ рдкреНрд░рддреНрдпрдп рдЬреЛрдбрд╝рд╛ рдЬрд╛ рд╕рдХрддрд╛ рд╣реИ)
        string descriptionText = component.Description;
        if (TextParser.IsValidLocalizableText(descriptionText))
        {
            string key = TextParser.GenerateKeyForObject(gameObject, descriptionText, settings.keyGenerationMode) + "_description";
            string source = TextParser.GetSourceStringForObject(gameObject);
            yield return (descriptionText, key, source);
        }
    }
}
#endif
