using Ankonoanko.Localization; // [LocalizableField] に必要
using UnityEngine;
using System.Collections.Generic;
public class QuestManager : MonoBehaviour
{
    // パーサーはこのフィールドを見つけてキーを作成します
    [LocalizableField]
    private string defaultQuestFailedMessage = "クエストに失敗しました。";

    // カスタムキーを設定できます
    [LocalizableField("custom_quest_start_dialog")]
    public string startDialog = "冒険の準備はできていますか？";

    // リストで動作します
    [LocalizableField]
    private List<string> missionObjectives = new List<string> { "宝物を見つける" };

    // ネストされたクラスでも動作します！
    [System.Serializable]
    public class ItemInfo
    {
        [LocalizableField] public string Name;
        [LocalizableField] public string Description;
    }

    [LocalizableField]
    private ItemInfo magicSword;
}

