using Ankonoanko.Localization; // [LocalizableField]에 필요
using UnityEngine;
using System.Collections.Generic;
public class QuestManager : MonoBehaviour
{
    // 파서가 이 필드를 찾아 키를 생성합니다.
    [LocalizableField]
    private string defaultQuestFailedMessage = "퀘스트에 실패했습니다.";

    // 사용자 지정 키를 설정할 수 있습니다.
    [LocalizableField("custom_quest_start_dialog")]
    public string startDialog = "모험을 떠날 준비가 되셨습니까?";

    // 목록과 함께 작동합니다.
    [LocalizableField]
    private List<string> missionObjectives = new List<string> { "보물 찾기" };

    // 그리고 중첩 클래스와도 함께 작동합니다!
    [System.Serializable]
    public class ItemInfo
    {
        [LocalizableField] public string Name;
        [LocalizableField] public string Description;
    }

    [LocalizableField]
    private ItemInfo magicSword;
}

