using static Ankonoanko.Localization.LocalizationManager;
using Ankonoanko.Localization; // Wymagane dla [OnLanguageChange]
using UnityEngine;
using TMPro;
using System.Collections.Generic;
public class PlayerHUD : MonoBehaviour
{
    [SerializeField] private TMP_Text scoreText;
    private int score = 100;
    private string playerName = "Alex";

    void Start()
    {
        UpdateUI(); // Początkowa aktualizacja
    }

    // Ta metoda będzie wywoływana automatycznie po zmianie języka
    [OnLanguageChange]
    void UpdateUI()
    {
        // 1. Indeksowane symbole zastępcze (jak w string.Format)
        // Klucz: "score_label", Tekst w pliku: "Wynik: {0}"
        scoreText.text = _("score_label", score); // Wynik: "Wynik: 100"

        // 2. Nazwane symbole zastępcze (zalecane dla czytelności)
        // Klucz: "welcome_message", Tekst: "Witaj, {username}!"
        string welcomeText = _("welcome_message", new { username = playerName });

        // W złożonych przypadkach lub dla wysokiej wydajności
        var args = new Dictionary<string, object> { { "username", playerName } };
        string welcomeText2 = _("welcome_message", args);
    }
}

