using static Ankonoanko.Localization.LocalizationManager;
using Ankonoanko.Localization; // Necess√°rio para [OnLanguageChange]
using UnityEngine;
using TMPro;
using System.Collections.Generic;
public class PlayerHUD : MonoBehaviour
{
    [SerializeField] private TMP_Text scoreText;
    private int score = 100;
    private string playerName = "Alex";

    void Start()
    {
        UpdateUI(); // Atualiza√ß√£o inicial
    }

    // Este m√©todo ser√° chamado automaticamente quando o idioma mudar
    [OnLanguageChange]
    void UpdateUI()
    {
        // 1. Placeholders indexados (como em string.Format)
        // Chave: "score_label", Texto no arquivo: "Score: {0}"
        scoreText.text = _("score_label", score); // Resultado: "Score: 100"

        // 2. Placeholders nomeados (recomendado para legibilidade)
        // Chave: "welcome_message", Texto: "Welcome, {username}!"
        string welcomeText = _("welcome_message", new { username = playerName });

        // Para casos complexos ou alto desempenho
        var args = new Dictionary<string, object> { { "username", playerName } };
        string welcomeText2 = _("welcome_message", args);
    }
}

